import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { Link, useNavigate } from "react-router-dom";

function MySubmissions() {
  const { user, token } = useAuth();
  const [submissions, setSubmissions] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    async function fetchSubmissions() {
      try {
        const res = await axios.get("http://localhost:5000/submissions/mine", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSubmissions(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load submissions");
      }
    }

    fetchSubmissions();
  }, [user, token, navigate]);

  if (!user) return null;

  return (
    <div>
      <h2>My Submissions</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {submissions.length === 0 ? (
        <p>No submissions yet.</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Problem</th>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Status</th>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Language</th>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Time</th>
            </tr>
          </thead>
          <tbody>
            {submissions.map((s) => (
              <tr key={s._id}>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>
                  <Link to={`/problems/${s.problem.slug}`}>
                    {s.problem.title}
                  </Link>
                </td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>{s.status}</td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>{s.language}</td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>
                  {new Date(s.createdAt).toLocaleString()}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default MySubmissions;
