// src/pages/ContestsList.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function ContestsList() {
  const [contests, setContests] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    async function fetchContests() {
      try {
        const res = await axios.get("http://localhost:5000/contests");
        setContests(res.data);
      } catch (err) {
        console.error("Error fetching contests:", err);
      } finally {
        setLoading(false);
      }
    }
    fetchContests();
  }, []);

  return (
    <div style={{ maxWidth: 900, margin: "0 auto" }}>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: 20,
          alignItems: "center",
        }}
      >
        <h2>Contests</h2>

        {/* Only show button if user logged in */}
        {user && (
          <Link
            to="/contests/create"
            style={{
              padding: "8px 14px",
              backgroundColor: "#0055ff",
              color: "white",
              textDecoration: "none",
              borderRadius: "6px",
              fontWeight: "bold",
            }}
          >
            + Add Contest
          </Link>
        )}
      </div>

      {loading ? (
        <p>Loading contests...</p>
      ) : contests.length === 0 ? (
        <p>No contests yet.</p>
      ) : (
        <div>
          {contests.map((contest) => (
            <div
              key={contest._id}
              style={{
                border: "1px solid #ddd",
                padding: 15,
                borderRadius: 8,
                marginBottom: 15,
                background: "#fafafa",
              }}
            >
              <h3>{contest.name}</h3>
              <p>{contest.description}</p>
              <p>
                <b>Start:</b> {new Date(contest.startTime).toLocaleString()}
                <br />
                <b>End:</b> {new Date(contest.endTime).toLocaleString()}
              </p>
              <Link
                to={`/contests/${contest._id}`}
                style={{
                  textDecoration: "none",
                  color: "#007bff",
                  fontWeight: "bold",
                }}
              >
                View Contest â†’
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ContestsList;
