import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import Editor from "@monaco-editor/react";

function ProblemPage() {
  const { slug } = useParams();
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState("// Write your solution here");
  const [message, setMessage] = useState("");
  const [runResult, setRunResult] = useState(null);
  const [running, setRunning] = useState(false);
  const { user, token } = useAuth();

  useEffect(() => {
    fetch(`http://localhost:5000/problems/${slug}`)
      .then((res) => res.json())
      .then((data) => setProblem(data))
      .catch((err) => console.error(err));
  }, [slug]);

  async function handleSubmit() {
    setMessage("");
    setRunResult(null);

    if (!user) {
      setMessage("You must be logged in to submit.");
      return;
    }

    if (!code.trim()) {
      setMessage("Code cannot be empty.");
      return;
    }

    try {
      const res = await axios.post(
        "http://localhost:5000/submissions",
        {
          problemId: problem._id,
          code,
          language: "javascript",
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage(`Submission saved with status: ${res.data.status}`);
    } catch (err) {
      console.error(err);
      setMessage(
        err.response?.data?.message || "Error submitting solution."
      );
    }
  }

  async function handleRun() {
    setMessage("");
    setRunResult(null);

    if (!user) {
      setMessage("You must be logged in to run code.");
      return;
    }

    if (!code.trim()) {
      setMessage("Code cannot be empty.");
      return;
    }

    try {
      setRunning(true);

      // 63 = JavaScript (Node.js) language_id in Judge0
      const res = await axios.post(
        "http://localhost:5000/judge/run",
        {
          code,
          languageId: 63,
          stdin: "", // later, you can allow custom input
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setRunResult(res.data);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Error running code.");
    } finally {
      setRunning(false);
    }
  }

  if (!problem) return <p>Loading problem...</p>;

  return (
    <div>
      <h2>{problem.title}</h2>
      <p>
        <b>Difficulty:</b> {problem.difficulty}
      </p>

      <h3>Description</h3>
      <p>{problem.description}</p>

      <h3>Examples</h3>
      {problem.exampleTests.map((ex, i) => (
        <pre key={i} style={{ background: "#eee", padding: 10 }}>
          <b>Input:</b> {ex.input}
          {"\n"}
          <b>Output:</b> {ex.output}
        </pre>
      ))}

      <h3>Your Code</h3>
      <div style={{ border: "1px solid #ccc", marginBottom: 10 }}>
        <Editor
          height="300px"
          defaultLanguage="javascript"
          value={code}
          onChange={(value) => setCode(value || "")}
          options={{
            fontSize: 14,
            minimap: { enabled: false },
          }}
        />
      </div>

      <button
        style={{ marginTop: 10, padding: "10px 20px", fontSize: 16 }}
        onClick={handleRun}
        disabled={running}
      >
        {running ? "Running..." : "Run Code"}
      </button>

      <button
        style={{ marginTop: 10, marginLeft: 10, padding: "10px 20px", fontSize: 16 }}
        onClick={handleSubmit}
      >
        Submit
      </button>

      {message && (
        <p style={{ marginTop: 10, color: "blue" }}>
          {message}
        </p>
      )}

      {runResult && (
        <div style={{ marginTop: 20 }}>
          <h3>Run Result</h3>
          <p>
            <b>Status:</b> {runResult.status?.description}
          </p>

          {runResult.stdout && (
            <>
              <h4>Output:</h4>
              <pre
                style={{
                  background: "#111",
                  color: "#0f0",
                  padding: 10,
                  whiteSpace: "pre-wrap",
                }}
              >
                {runResult.stdout}
              </pre>
            </>
          )}

          {runResult.stderr && (
            <>
              <h4>Error:</h4>
              <pre
                style={{
                  background: "#200",
                  color: "#f55",
                  padding: 10,
                  whiteSpace: "pre-wrap",
                }}
              >
                {runResult.stderr}
              </pre>
            </>
          )}

          {runResult.compile_output && (
            <>
              <h4>Compile Output:</h4>
              <pre
                style={{
                  background: "#222",
                  color: "#ff0",
                  padding: 10,
                  whiteSpace: "pre-wrap",
                }}
              >
                {runResult.compile_output}
              </pre>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default ProblemPage;
