// src/pages/ProblemPage.js
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import Editor from "@monaco-editor/react";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

const languageToJudge0Id = {
  javascript: 63, // Node.js
  python: 71,     // Python 3
  cpp: 54,        // C++ (GCC 9.2.0)
};

function ProblemPage() {
  const { slug, contestId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { user, token } = useAuth();

  const [problem, setProblem] = useState(null);
  const [loadingProblem, setLoadingProblem] = useState(true);
  const [error, setError] = useState("");

  const [language, setLanguage] = useState("javascript");
  const [code, setCode] = useState("// Loading starter code...\n");
  const [customInput, setCustomInput] = useState("");
  const [output, setOutput] = useState("");

  const [running, setRunning] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  // ---------- Load problem by slug ----------
  useEffect(() => {
    async function fetchProblem() {
      try {
        setLoadingProblem(true);
        setError("");
        const res = await axios.get(`${API_URL}/problems/${slug}`);
        const p = res.data;
        setProblem(p);

        const starter =
          p.starterCode?.[language] ||
          p.starterCode ||
          "// Write your solution here\n";
        setCode(starter);

        if (p.exampleTests && p.exampleTests.length > 0) {
          setCustomInput(p.exampleTests[0].input || "");
        } else {
          setCustomInput("");
        }
      } catch (err) {
        console.error("Fetch problem error:", err);
        setError("Failed to load problem.");
      } finally {
        setLoadingProblem(false);
      }
    }

    if (slug) {
      fetchProblem();
    }
  }, [slug, language]);

  // ---------- Run code (uses /submissions/run) ----------
  const handleRun = async () => {
    if (!code.trim()) {
      setOutput("Please write some code first.");
      return;
    }

    try {
      setRunning(true);
      setOutput("Running...");

      // Use the mapping here:
      const languageId = languageToJudge0Id[language];

      const res = await axios.post(`${API_URL}/submissions/run`, {
        code,
        languageId, // send the numeric ID!
        input: customInput || "",
      });

      const data = res.data || {};
      if (data.error) {
        setOutput(`Error:\n${data.error}`);
      } else if (data.output) {
        setOutput(data.output);
      } else {
        setOutput("No output received from runner.");
      }
    } catch (err) {
      console.error("Run error:", err.response?.data || err.message);

      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        err.message ||
        "Error executing code.";
      setOutput(msg);
    } finally {
      setRunning(false);
    }
  };

  // ---------- Submit solution (auto-judge via /submissions) ----------
  const handleSubmit = async () => {
    if (!user) {
      navigate("/login", {
        state: {
          from: location.pathname,
          message: "You must be logged in to submit solutions.",
        },
      });
      return;
    }

    if (!problem) {
      setOutput("Problem not loaded yet.");
      return;
    }

    try {
      setSubmitting(true);
      setOutput("Submitting...");

      const body = {
        code,
        language,
        problemId: problem._id,
      };

      if (contestId) {
        body.contestId = contestId;
      }

      const res = await axios.post(`${API_URL}/submissions`, body, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const status = res.data?.status || "Submitted";
      setOutput(`Result: ${status}`);
    } catch (err) {
      console.error("Submit error:", err.response?.data || err.message);
      const msg =
        err.response?.data?.message ||
        err.response?.data?.error ||
        err.message ||
        "Error while submitting.";
      setOutput(msg);
    } finally {
      setSubmitting(false);
    }
  };

  // Use an example test as custom input
  const handleUseExample = (test) => {
    setCustomInput(test.input || "");
    setOutput("");
  };

  // ---------- Helpers ----------
  const difficultyClass = useMemo(() => {
    if (!problem?.difficulty) return "pill pill-muted";
    const d = problem.difficulty.toLowerCase();
    if (d === "easy") return "pill pill-easy";
    if (d === "medium") return "pill pill-medium";
    if (d === "hard") return "pill pill-hard";
    return "pill pill-muted";
  }, [problem]);

  if (loadingProblem) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-muted">Loading problem...</p>
        </div>
      </div>
    );
  }

  if (error || !problem) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-error">{error || "Problem not found."}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <div className="problem-layout">
        {/* LEFT: Problem description + tests */}
        <div className="problem-left">
          <div className="page-card">
            <h1 className="problem-title">{problem.title}</h1>
            <div style={{ marginTop: "4px", marginBottom: "10px" }}>
              <span className={difficultyClass}>
                {problem.difficulty || "Unknown"}
              </span>
            </div>

            <div className="problem-description">
              <h2>Description</h2>
              <p>{problem.description}</p>

              {problem.exampleTests && problem.exampleTests.length > 0 && (
                <>
                  <h2 style={{ marginTop: "16px" }}>Examples</h2>
                  <div className="examples-list">
                    {problem.exampleTests.map((test, idx) => (
                      <div className="example-card" key={idx}>
                        <div className="example-header">
                          <span className="example-title">
                            Example {idx + 1}
                          </span>
                          <button
                            type="button"
                            className="btn-ghost-sm"
                            onClick={() => handleUseExample(test)}
                          >
                            Use as Input
                          </button>
                        </div>
                        <pre className="problem-example">
                          {test.input && (
                            <>
                              <strong>Input:</strong> {test.input}
                              {"\n"}
                            </>
                          )}
                          {test.output && (
                            <>
                              <strong>Output:</strong> {test.output}
                            </>
                          )}
                        </pre>
                        {test.explanation && (
                          <p className="example-explanation">
                            <strong>Explanation:</strong> {test.explanation}
                          </p>
                        )}
                      </div>
                    ))}
                  </div>
                </>
              )}
            </div>
          </div>
        </div>

        {/* RIGHT: Editor + I/O */}
        <div className="problem-right">
          <div className="page-card">
            <div className="problem-controls">
              <select
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
                className="select-control"
                style={{ maxWidth: 180 }}
              >
                <option value="javascript">JavaScript</option>
                <option value="python">Python</option>
                <option value="cpp">C++</option>
              </select>

              <button
                className="btn-run"
                onClick={handleRun}
                disabled={running || submitting}
              >
                {running ? "Running..." : "Run Code"}
              </button>

              <button
                className="btn-submit"
                onClick={handleSubmit}
                disabled={running || submitting}
              >
                {submitting ? "Submitting..." : "Submit"}
              </button>
            </div>

            <Editor
              height="55vh"
              theme="vs-dark"
              language={language}
              value={code}
              onChange={(value) => setCode(value ?? "")}
            />

            <div className="io-container">
              <div className="io-column">
                <div className="io-header">Custom Input</div>
                <textarea
                  className="input-box"
                  value={customInput}
                  onChange={(e) => setCustomInput(e.target.value)}
                  placeholder="Enter input that your program reads from stdin..."
                />
              </div>

              <div className="io-column">
                <div className="io-header">Output</div>
                <pre className="output-box">
                  {output || "Output will appear here..."}
                </pre>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProblemPage;
