// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

function Profile() {
  const { token } = useAuth();
  const [profile, setProfile] = useState(null);
  const [errMsg, setErrMsg] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function load() {
      if (!token) {
        setErrMsg("You must be logged in to view profile.");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setErrMsg("");

        const res = await api.get("/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(res.data);
      } catch (err) {
        console.error("Profile load error:", err.response || err.message);
        setErrMsg("Failed to load profile.");
      } finally {
        setLoading(false);
      }
    }

    load();
  }, [token]);

  if (loading) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-muted">Loading profile...</p>
        </div>
      </div>
    );
  }

  if (errMsg || !profile) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-error">{errMsg || "No profile data found."}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">Profile</h1>
        <p className="page-subtitle">
          Member since{" "}
          {profile.createdAt
            ? new Date(profile.createdAt).toLocaleDateString()
            : "-"}
        </p>

        <div className="profile-header">
          <div className="profile-avatar">
            {profile.username?.charAt(0).toUpperCase()}
          </div>
          <div>
            <h2>{profile.username}</h2>
            <p className="page-muted">{profile.email}</p>
          </div>
        </div>

        <div className="profile-stats-cards" style={{ marginTop: "1rem" }}>
          <div className="profile-stat-card">
            <div className="stat-label">Rating</div>
            <div className="stat-value">{profile.rating ?? 1500}</div>
          </div>
          <div className="profile-stat-card">
            <div className="stat-label">Total Solved</div>
            <div className="stat-value">{profile.totalSolved ?? 0}</div>
          </div>
          <div className="profile-stat-card">
            <div className="stat-label">Duels</div>
            <div className="stat-value">
              {(profile.duelWins ?? 0)}W / {(profile.duelLosses ?? 0)}L
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
