// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

function Profile() {
  const { token } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [debug, setDebug] = useState(null);

  useEffect(() => {
    let mounted = true;
    async function load() {
      setLoading(true);
      setError("");
      setDebug(null);

      try {
        const headers = {};
        if (token) headers.Authorization = `Bearer ${token}`;

        const res = await axios.get("http://localhost:5000/users/profile", { headers });
        if (mounted) {
          setProfile(res.data);
          setDebug({ status: res.status, headers: res.headers });
        }
      } catch (err) {
        console.error("Profile load error:", err);
        const status = err.response?.status;
        const body = err.response?.data;
        const msg = body?.message || err.message || "Unknown error";
        if (mounted) {
          setError(`Failed to load profile. ${status ? `Status ${status}: ` : ""}${msg}`);
          setDebug({
            status,
            body,
            request: {
              url: err.config?.url,
              method: err.config?.method,
              headers: err.config?.headers,
            },
          });
        }
      } finally {
        if (mounted) setLoading(false);
      }
    }

    load();
    return () => (mounted = false);
  }, [token]);

  if (loading) return <div style={{ padding: 24 }}>Loading profile…</div>;
  if (error)
    return (
      <div style={{ padding: 24, color: "crimson" }}>
        <div>{error}</div>
        <details style={{ marginTop: 10, whiteSpace: "pre-wrap" }}>
          <summary>Debug info</summary>
          {JSON.stringify(debug, null, 2)}
        </details>
      </div>
    );
  if (!profile) return <div style={{ padding: 24 }}>No profile data.</div>;

  const initials = (profile.username || "U")
    .split(" ")
    .map((s) => s[0])
    .slice(0, 2)
    .join("")
    .toUpperCase();

  return (
    <div style={{ maxWidth: 980, margin: "24px auto", padding: 20 }}>
      <div
        style={{
          display: "flex",
          gap: 20,
          alignItems: "center",
          background: "#fff",
          borderRadius: 10,
          boxShadow: "0 4px 18px rgba(13,26,38,0.08)",
          padding: 20,
          border: "1px solid #eef2f7",
        }}
      >
        <div
          style={{
            minWidth: 96,
            minHeight: 96,
            width: 96,
            height: 96,
            borderRadius: 16,
            background: "#4f46e5",
            color: "#fff",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 32,
            fontWeight: 700,
            boxShadow: "inset 0 -6px 18px rgba(0,0,0,0.08)",
          }}
          aria-hidden
        >
          {initials}
        </div>

        <div style={{ flex: 1 }}>
          <div style={{ display: "flex", justifyContent: "space-between", gap: 12, alignItems: "start" }}>
            <div>
              <h2 style={{ margin: 0, fontSize: 22 }}>{profile.username || "User"}</h2>
              <p style={{ margin: "6px 0 0", color: "#555" }}>{profile.email}</p>
              {profile.bio && <p style={{ marginTop: 8, color: "#444" }}>{profile.bio}</p>}
            </div>

            <div style={{ display: "flex", gap: 8 }}>
              <Link
                to="/submissions"
                style={{
                  background: "#06b6d4",
                  color: "#fff",
                  padding: "8px 14px",
                  borderRadius: 8,
                  textDecoration: "none",
                  fontWeight: 600,
                }}
              >
                My Submissions
              </Link>

              <Link
                to="/profile/edit"
                style={{
                  border: "1px solid #cbd5e1",
                  background: "#fff",
                  color: "#111827",
                  padding: "8px 14px",
                  borderRadius: 8,
                  textDecoration: "none",
                }}
              >
                Edit
              </Link>
            </div>
          </div>

          <div
            style={{
              marginTop: 18,
              display: "flex",
              gap: 12,
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <Stat label="Problems Solved" value={profile.stats?.solved || 0} />
            <Stat label="Submissions" value={profile.stats?.submissions || 0} />
            <Stat label="Rank" value={profile.stats?.rank || "—"} />
            <div style={{ color: "#6b7280", fontSize: 13 }}>
              Joined: {profile.createdAt ? new Date(profile.createdAt).toLocaleDateString() : "—"}
            </div>
          </div>
        </div>
      </div>

      <section style={{ marginTop: 20 }}>
        <h3 style={{ marginBottom: 10 }}>Recent Activity</h3>
        {profile.recentSubmissions?.length ? (
          <div style={{ display: "grid", gap: 10 }}>
            {profile.recentSubmissions.map((s) => (
              <div
                key={s._id}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  padding: 12,
                  borderRadius: 8,
                  background: "#fafafa",
                  border: "1px solid #f1f5f9",
                }}
              >
                <div style={{ color: "#0f172a" }}>
                  <div style={{ fontWeight: 600 }}>{s.problemTitle}</div>
                  <div style={{ color: "#6b7280", fontSize: 13 }}>
                    {s.language} — {s.status}
                  </div>
                </div>
                <div style={{ color: "#6b7280", fontSize: 13 }}>
                  {new Date(s.createdAt).toLocaleString()}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div style={{ color: "#6b7280" }}>No recent activity.</div>
        )}
      </section>
    </div>
  );
}

function Stat({ label, value }) {
  return (
    <div style={{ minWidth: 140, background: "#fff", padding: 12, borderRadius: 8, border: "1px solid #eef2f7" }}>
      <div style={{ fontSize: 18, fontWeight: 700, color: "#111827" }}>{value}</div>
      <div style={{ color: "#6b7280", marginTop: 6, fontSize: 13 }}>{label}</div>
    </div>
  );
}

export default Profile;
